INSERT INTO `about` (`id`, `noidung`) VALUES
(1, '<h3><tt><small>Giới Thiệu Về Shop</small></tt></h3>\r\n\r\n<h3><tt><small>I.LỜI GIỚI THIỆU</span></strong></small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Ch&uacute;ng t&ocirc;i l&agrave; nh&agrave; cung cấp giống,sản phẩm trầm hương cao cấp cho thị trường Việt Nam v&agrave; thế giới trong suốt hơn 20 năm qua. Kh&aacute;ch h&agrave;ng của ch&uacute;ng t&ocirc;i trải d&agrave;i từ Nam ra Bắc, từ Nhật Bản, Trung Quốc, Đ&agrave;i Loan cho đến nước Mỹ xa x&ocirc;i.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Sứ mệnh l&agrave; khơi nguồn năng lượng sống hạnh ph&uacute;c cho mọi người. Với ước mơ đ&oacute;, ch&uacute;ng t&ocirc;i lu&ocirc;n cố gắng ở mức cao nhất để phục vụ c&aacute;c bạn bằng sản phẩm tốt nhất về trầm hương &ndash; một sản phẩm phục vụ sức khỏe cả về thể chất lẫn tinh thần. Về sức khỏe, trầm hương c&oacute; những đặc điểm dược l&yacute; rất tốt cho cơ thể con người. Về tinh thần, trầm hương l&agrave; sản vật gi&uacute;p con người c&oacute; cảm nhận gần với Phật giới, với vũ trụ bao la nhất.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Mục ti&ecirc;u của&nbsp;l&agrave; đồng h&agrave;nh c&ugrave;ng bạn trong cuộc sống sinh hoạt hằng ng&agrave;y cũng như những sinh hoạt t&acirc;m linh m&agrave; bạn đang hướng tới.Bầu kh&ocirc;ng kh&iacute; thoang thoảng hương trầm vừa gi&uacute;p l&agrave;m trong sạch m&ocirc;i trường xung quanh bạn, vừa gi&uacute;p bạn sảng kho&aacute;i tinh thần trong cả ng&agrave;y l&agrave;m việc v&agrave; trợ lực cho bạn trong những l&uacute;c bạn cần thư gi&atilde;n trong tĩnh lặng.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Với kinh nghị&ecirc;m hơn&nbsp;20 năm trong nghề, ch&uacute;ng t&ocirc;i&nbsp;lu&ocirc;n lu&ocirc;m&nbsp;chọn lọc v&agrave; mang đến cho bạn c&aacute;c nh&oacute;m sản phẩm trầm hương cao cấp. Đến với <strong>SHOP</strong>&nbsp;bạn chắc chắn sẽ cảm nhận độ tinh khiết thật sự của trầm hương !</small></tt></h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3><tt><small><strong>II.QUYỀN LỢI:</strong></small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Sản phẩm cam kết 100%&nbsp;chất lượng, đ&uacute;ng gi&aacute;, đ&uacute;ng mi&ecirc;u tả.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Ho&agrave;n tiền 200% nếu ph&aacute;t hiện h&agrave;ng k&eacute;m chất lượng.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Bảo h&agrave;nh 12 th&aacute;ng đối với mọi sản phẩm.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Miễn ph&iacute; ship to&agrave;n quốc tất cả c&aacute;c sản phẩm. Kiểm tra h&agrave;ng mới thanh to&aacute;n.</small></tt></h3>\r\n\r\n<h3><tt><small>&nbsp; Bộ phận tư vấn&nbsp;nhiệt t&igrave;nh, chuy&ecirc;n nghiệp.</small></tt></h3>'),
(2, '<h3><tt><small>Ch&iacute;nh S&aacute;ch Bảo H&agrave;nh</small></tt></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<h3><tt><small><strong>I. BẢO H&Agrave;NH</strong></small></tt></h3>\r\n	</li>\r\n	<li>\r\n	<h3><tt><small>-Ch&uacute;ng t&ocirc;i&nbsp;l&agrave; thương hiệu&nbsp;cung cấp sản phẩm&nbsp;đ&atilde; khẳng định được t&ecirc;n tuổi&nbsp;tr&ecirc;n&nbsp;khắp cả nước. Ch&uacute;ng t&ocirc;i ch&uacute; trọng giải quyết vấn đề nhanh gọn nhất v&agrave; tốt nhất cho kh&aacute;ch h&agrave;ng.Tất cả c&aacute;c sản phẩm đ&atilde; được chọn lọc v&agrave; kiểm định chất lượng. Trước khi gửi h&agrave;ng cho bạn, ch&uacute;ng t&ocirc;i lại kiểm tra lại lần nữa để hạn chế tối đa khả năng lỗi của sản phẩm chuyển đến tay bạn. Sản phẩm phải bảo h&agrave;nh sẽ được hướng dẫn chuyển về Shop v&agrave; được ch&uacute;ng t&ocirc;i&nbsp;giải quyết nhanh nhất c&oacute; thể.</small></tt></h3>\r\n	</li>\r\n	<li>\r\n	<h3><tt><small>-Thời gian bảo h&agrave;nh cho mọi sản phẩm l&agrave;&nbsp;<strong><em>12 th&aacute;ng</em></strong>.</small></tt></h3>\r\n	</li>\r\n</ul>'),
(3, '<p dir="ltr"><strong>L&Agrave;M SAO ĐỂ MUA S&Aacute;CH MIỄN PH&Iacute;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir="ltr"><strong>C&aacute;ch 1 : </strong></p>\r\n\r\n<p dir="ltr"><strong>B1: Click v&agrave;o chia sẽ nhận hoa hồng </strong></p>\r\n\r\n<p dir="ltr"><strong>B2 : Coppy link ph&iacute;a dưới share link n&agrave;y cho mọi người đăng k&yacute; bạn sẽ nhận được hoa hồng được Cộng trực tiếp v&agrave;o t&agrave;i khoản để mua s&aacute;ch.</strong></p>\r\n\r\n<p dir="ltr"><strong><img src="https://lh5.googleusercontent.com/fqMHW08e7sn2vKZegiVUO2-YVnaKWMzyg6pHy_4hWW_Z3xH3OWXQNSP08IVn-LdNfVAVUoEgScDXI_3eUO3lYYuTc1WZcKPUR9jXqgYH3aA8i5ibzDVoJhLlW-6z1_BEIK8WHxiw" style="height:376px; width:602px" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir="ltr"><strong>C&aacute;ch 2 : </strong></p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr"><strong>M&atilde; giới thiệu ch&iacute;nh l&agrave; t&ecirc;n đăng nhập của t&agrave;i khoản của bạn.</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr"><strong>VD : Đ&acirc;y l&agrave; m&atilde; giới thiệu ri&ecirc;ng của mỗi người.</strong></p>\r\n\r\n<p dir="ltr"><strong><img src="https://lh4.googleusercontent.com/HmIht3iwkWMJAcPVO5EGvA6-xCqM78rXQn3efw4glAl0v7yZBrKpTn2oyLSvJnehqDNLnNEZd3QCllfGaRZ3bL_Qoi-mjdpxOPOv14r9U2cVNrs3DzLDNn8k5YMuAA0JMa_7CAYL" style="height:192px; width:602px" /></strong></p>\r\n\r\n<p><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr"><strong>Hướng dẫn người mua s&aacute;ch nhập m&atilde; v&agrave;o </strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr"><strong><img src="https://lh5.googleusercontent.com/7vAkYiadOqVjSKRa-xkPx9QyGY0dWlOC4N0lKfH8zGFvieQQiLsQI3DeM2mVPfOkt_tfubKuFsLCuNWW9PlxbbG-cJt-SWQsfavimebYLdkHIMagatPZ3B2OXfDC3w3uX2YrxKLZ" style="height:376px; width:602px" /></strong></p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p dir="ltr"><strong>C&aacute;ch kiểm tra đơn h&agrave;ng v&agrave; khi n&agrave;o th&igrave; nhận tiền khuyến m&atilde;i :</strong></p>\r\n\r\n<ul